package operators;

public class Left_n_Right_ShiftOpr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 8;
		
		System.out.println(num<<2); //32
		System.out.println(num>>2); //2
		System.out.println(num>>>2); //2
		System.out.println((~num)>>>2); 

	}

}
